mapper.select:
select

from pd_form_template_attributes t
where t.id = #{value}

mapper.update:
update pd_form_template_attributes
template_id = #{templateId}, attributes_name = #{attributesName}, element_attribute = #{elementAttribute}, element_type = #{elementType}, element_order = #{elementOrder}, describe = #{describe}, is_null = #{isNull}, element_option = #{elementOption}, element_format = #{elementFormat}, is_show = #{isShow},
where id = #{id}
